class Qwen2_5_VLMLP(nn.Module):
    def __init__(self, config, bias: bool = False):
        super().__init__()
        self.hidden_size = config.hidden_size #
        self.intermediate_size = config.intermediate_size
        self.gate_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=bias) # é—¨æ§æŠ•å½±ï¼Œæ§åˆ¶ä¿¡æ¯æµåŠ¨
        self.up_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=bias) # å‡ç»´æŠ•å½±ï¼ŒæŠŠç‰¹å¾æ˜ å°„åˆ°æ›´é«˜ç»´åº¦
        self.down_proj = nn.Linear(self.intermediate_size, self.hidden_size, bias=bias) # é™ç»´æŠ•å½±ï¼Œå§ç‰¹å¾æ˜ å°„å›åŸå§‹ç»´åº¦
        self.act_fn = ACT2FN[config.hidden_act] # æ¿€æ´»å‡½æ•°ï¼Œå¯¹ç‰¹å¾è¿›è¡Œéçº¿æ€§å˜æ¢

    def forward(self, hidden_state):
        return self.down_proj(self.act_fn(self.gate_proj(hidden_state)) * self.up_proj(hidden_state))

