class Qwen2_5_VisionPatchEmbed(nn.Module):
    def __init__(
        self,
        patch_size: int = 14,
        temporal_patch_size: int = 2,
        in_channels: int = 3,
        embed_dim: int = 1152,
    ) -> None:
        '''
        - å›¾åƒè¾“å…¥ï¼š[batch_size, channels, height, width]
        - è§†é¢‘è¾“å…¥ï¼š[batch_size, channels, frames, height, width]
        '''
        super().__init__()
        self.patch_size = patch_size
        self.temporal_patch_size = temporal_patch_size
        self.in_channels = in_channels
        self.embed_dim = embed_dim

        kernel_size = [temporal_patch_size, patch_size, patch_size] # 3Då·ç§¯æ ¸ï¼Œç”¨äºå°†è¾“å…¥çš„3Dç‰¹å¾æ˜ å°„åˆ°2Dç‰¹å¾ï¼Œèƒ½åŒæ—¶å¤„ç†è§†é¢‘å’Œå›¾åƒ
        self.proj = nn.Conv3d(in_channels, embed_dim, kernel_size=kernel_size, stride=kernel_size, bias=False) # æ­¥é•¿ç­‰äºpatchå¤§å°ï¼Œä¿è¯æ— é‡å åˆ†å‰²è¾“å…¥

    def forward(self, hidden_states: torch.Tensor) -> torch.Tensor:
        '''
        è¾“å…¥ï¼š hidden_states (shape: [batch_size * num_patches, channels, temporal_patch_size, patch_size, patch_size])
            1. ç±»å‹è½¬æ¢ï¼šå°†è¾“å…¥è½¬æ¢ä¸ºä¸å·ç§¯æ ¸ç›¸åŒçš„æ•°æ®ç±»å‹
            2. 3Då·ç§¯ï¼šä½¿ç”¨3Då·ç§¯å°†æ¯ä¸ªè¡¥ä¸è½¬æ¢ä¸ºç‰¹å¾å‘é‡
            3. å½¢çŠ¶è°ƒæ•´ï¼šå°†è¾“å‡ºè°ƒæ•´ä¸ºç‰¹å¾åºåˆ—æ ¼å¼
        è¾“å‡ºï¼š hidden_states (shape: [batch_size * num_patches, embed_dim])
        '''
        target_dtype = self.proj.weight.dtype
        hidden_states = hidden_states.view(
            -1, self.in_channels, self.temporal_patch_size, self.patch_size, self.patch_size
        )
        hidden_states = self.proj(hidden_states.to(dtype=target_dtype)).view(-1, self.embed_dim)
        return hidden_states

'''
visionçš„RotaryEmbeddingå±‚
å°†è¾“å…¥çš„ç‰¹å¾åºåˆ—é‡è½¬æ¢ä¸ºæ—‹è½¬ç¼–ç é‡ï¼Œç”¨äºæ¨¡å‹çš„è‡ªæ³¨æ„åŠ›æœºåˆ¶
'''
class Qwen2_5_VisionRotaryEmbedding(nn.Module):
    inv_freq: torch.Tensor  # fix linting for `register_buffer`

    def __init__(self, dim: int, theta: float = 10000.0) -> None:
        super().__init__()
        self.dim = dim
        self.theta = theta
        inv_freq = 1.0 / (theta ** (torch.arange(0, dim, 2, dtype=torch.float) / dim))
        self.register_buffer("inv_freq", inv_freq, persistent=False)

    def forward(self, seqlen: int) -> torch.Tensor:
        seq = torch.arange(seqlen, device=self.inv_freq.device, dtype=self.inv_freq.dtype)
        freqs = torch.outer(seq, self.inv_freq)
        return freqs

